    import React, { useState, useEffect } from 'react';
    import { Link } from 'react-router-dom';

    // 상단 게시판 카테고리 모음
    const categories = ['자유게시판', '공감게시판', 'BEST게시판'];

    const MainPage = () => {
        
        // 처음 초기값 게시판을 자유게시판으로 만들기 위하 코드, 만약 '공감게시판'이면 새로고침할 때마다 공감게시판이 처음 보임
        const [selectedCategory, setSelectedCategory] = useState('자유게시판');
        const [allPosts, setAllPosts] = useState(null); // 모든 게시글 목록을 저장하는 상태
        const [filteredPosts, setFilteredPosts] = useState(null); // 선택된 카테고리에 맞는 게시글만 저장하는 상태
    
        
        useEffect(() => {
            const fetchPosts = async () => {
            try {
                
                const response = await fetch(`http://localhost:8000/`);
                
                if (!response.ok) {
                    throw new Error('Failed to fetch posts');
                }

                const data = await response.json();
                
                setAllPosts(data); // 전체 게시글 목록을 설정

            } catch (error) {
                console.error('Error fetching posts:', error);
            }};

            fetchPosts(); // fetchPosts 함수를 호출하여 데이터를 가져옵니다.
        }, []);


        useEffect(() => {
            // allPosts가 유효하고 선택된 카테고리가 있는 경우에만 필터링 수행
            if (allPosts && selectedCategory) {
                const filtered = allPosts.filter(post => post.category === selectedCategory);
                setFilteredPosts(filtered);
            }
        }, [allPosts, selectedCategory]);


        
        return (
            <div>
            
                <h1>메인 페이지</h1>
                <div style={{ marginBottom: '20px' }}>
                    {categories.map(category => (
                    <button 
                        key={category} 
                        onClick={() => setSelectedCategory(category)}
                        style={{ margin: '0 10px' }}
                    >
                        {category}
                    </button>
                    ))}
                </div>

            <Link to="/PostWrite">글쓰기</Link> {/* 글쓰기 버튼 또는 Link 말고 onClick 방식 둘 중 하나 사용 */}
            <Link to="/LoginPage">로그인</Link> {/* 로그인 버튼 추가 */}
            <Link to="/MyPage">마이페이지</Link> {/* 마이페이지 버튼 추가 */}


            <ul>
                {posts === null ? (
                    <li>Loading...</li>
                ) : (
                    posts.map(post => (
                        <li key={post.id}>
                            <h2>{post.title}</h2>
                            <p>{post.content}</p>
                        </li>
                    ))
                )}
            </ul>
            </div>
        );
    };

    export default MainPage;
