import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const RegisterPage = () => {
    const navigate = useNavigate(); // useNavigate 훅을 사용

    // 상태값을 이용하여 사용자 정보 설정
    const [id, setId] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [nickname, setNickname] = useState('');
    const [profileImage, setProfileImage] = useState(null); // 프로필 이미지 파일 상태 추가

    const handleSubmit = async (e) => {
      e.preventDefault();
  
      // FormData 객체 생성 및 데이터 추가
      const formData = new FormData();
      formData.append('user', JSON.stringify({ // 사용자 정보를 JSON 문자열로 변환하여 추가
          userid: id,
          password: password,
          nickname: nickname,
      }));
      formData.append('profileImage', profileImage);
  
      try {
          const response = await fetch('http://localhost:8080/api/auth/register', {
              method: 'POST',
              headers: {
                  // 'Content-Type' 헤더를 추가하여 multipart/form-data로 설정
                  'Content-Type' : 'multipart/form-data'
              },
              body: formData, // 프로필 이미지와 사용자 정보를 함께 전송
          });
  
          if (!response.ok) {
              throw new Error('Failed to register user');
          }
  
          const data = await response.json();
          console.log('Success:', data);
          alert('회원가입이 완료되었습니다.');
          navigate('/loginPage'); // 로그인 페이지로 이동
  
      } catch (error) {
          console.error('Error:', error);
      }
  };
  

    return (
        <div>
            <h2>회원가입</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>아이디:</label>
                    <input type="text" value={id} onChange={(e) => setId(e.target.value)} />
                </div>
                <div>
                    <label>닉네임:</label>
                    <input type="text" value={nickname} onChange={(e) => setNickname(e.target.value)} />
                </div>
                <div>
                    <label>비밀번호:</label>
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                </div>
                <div>
                    <label>비밀번호 확인:</label>
                    <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />
                </div>
                <div>
                    <label>프로필 이미지:</label>
                    <input type="file" onChange={(e) => setProfileImage(e.target.files[0])} /> {/* 파일 입력 필드 추가 */}
                </div>
                <button type="submit">가입하기</button>
            </form>
            <p>이미 계정이 있으신가요? <Link to="/loginPage">여기를 클릭하여 로그인하세요</Link>.</p>
        </div>
    );
};

export default RegisterPage;
