import React, { useState, useEffect } from 'react';


// 상단 게시판 카테고리 모음
const categories = ['자유게시판', '공감게시판', 'BEST게시판'];

const MainPage = () => {
    const [selectedCategory, setSelectedCategory] = useState('전체');
    const [posts, setPosts] = useState(null);

    // 각 게시판에 대한 예시 데이터
    // 예시 데이터를 사용하여 게시물 목록을 설정합니다.
    // 실제 애플리케이션에서는 API 호출을 통해 데이터를 가져와야 합니다.    
    const examplePosts = {
        '자유게시판': [
          { id: 1, category: '자유게시판', title: '첫 번째 자유게시판 게시물', content: '첫 번째 게시물 내용입니다.' },
          { id: 2, category: '자유게시판', title: '두 번째 자유게시판 게시물', content: '두 번째 게시물 내용입니다.' },
          { id: 3, category: '자유게시판', title: '세 번째 자유게시판 게시물', content: '세 번째 게시물 내용입니다.' }
        ],
        '공감게시판': [
          { id: 4, category: '공감게시판', title: '첫 번째 공감게시판 게시물', content: '첫 번째 게시물 내용입니다.' },
          { id: 5, category: '공감게시판', title: '두 번째 공감게시판 게시물', content: '두 번째 게시물 내용입니다.' },
          { id: 6, category: '공감게시판', title: '세 번째 공감게시판 게시물', content: '세 번째 게시물 내용입니다.' },
          { id: 7, category: '공감게시판', title: '네 번째 공감게시판 게시물', content: '네 번째 게시물 내용입니다.' },
          { id: 8, category: '공감게시판', title: '다섯 번째 공감게시판 게시물', content: '다섯 번째 게시물 내용입니다.' }
        ],
        'BEST게시판': [
          { id: 9, category: 'BEST게시판', title: '첫 번째 BEST게시판 게시물', content: '첫 번째 게시물 내용입니다.' },
          { id: 10, category: 'BEST게시판', title: '두 번째 BEST게시판 게시물', content: '두 번째 게시물 내용입니다.' },
          { id: 11, category: 'BEST게시판', title: '세 번째 BEST게시판 게시물', content: '세 번째 게시물 내용입니다.' },
          { id: 12, category: 'BEST게시판', title: '네 번째 BEST게시판 게시물', content: '네 번째 게시물 내용입니다.' },
          { id: 13, category: 'BEST게시판', title: '다섯 번째 BEST게시판 게시물', content: '다섯 번째 게시물 내용입니다.' },
          { id: 14, category: 'BEST게시판', title: '여섯 번째 BEST게시판 게시물', content: '여섯 번째 게시물 내용입니다.' },
          { id: 15, category: 'BEST게시판', title: '일곱 번째 BEST게시판 게시물', content: '일곱 번째 게시물 내용입니다.' },
          { id: 16, category: 'BEST게시판', title: '여덟 번째 BEST게시판 게시물', content: '여덟 번째 게시물 내용입니다.' }
        ]
    };
    
    useEffect(() => {
        // 선택된 카테고리에 해당하는 게시글 데이터를 설정합니다.
        setPosts(examplePosts[selectedCategory]);
    }, [selectedCategory, examplePosts]);

    /* 실제 호출에 쓸 코드
    useEffect(() => {
    // API를 호출하여 자유게시판의 게시글 데이터를 가져옵니다.
    const fetchPosts = async () => {
      try {
        // 예시 데이터 대신에 실제 API 호출을 수행해야 합니다.
        const response = await fetch('API_ENDPOINT_HERE');
        if (!response.ok) {
          throw new Error('Failed to fetch posts');
        }
        const data = await response.json();
        setPosts(data); // API에서 받은 데이터를 상태에 설정합니다.
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    fetchPosts(); // fetchPosts 함수를 호출하여 데이터를 가져옵니다.
    }, []);
    */



    return (
        <div>
        <h1>메인 페이지</h1>
        <div style={{ marginBottom: '20px' }}>
            {categories.map(category => (
            <button 
                key={category} 
                onClick={() => setSelectedCategory(category)}
                style={{ margin: '0 10px' }}
            >
                {category}
            </button>
            ))}
        </div>
        <ul>
            {posts === null ? (
                <li>Loading...</li>
            ) : (
                posts.map(post => (
                    <li key={post.id}>
                        <h2>{post.title}</h2>
                        <p>{post.content}</p>
                    </li>
                ))
            )}
        </ul>
        </div>
    );
};

export default MainPage;
