import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

const PostPage = () => {
  const { id } = useParams();

  // 예시 데이터를 사용하여 게시물 정보를 설정합니다.
  // 실제 애플리케이션에서는 API 호출을 통해 데이터를 가져와야 합니다.
  const post = {
    1: { title: '첫 번째 게시물', content: '첫 번째 게시물 내용입니다.' },
    2: { title: '두 번째 게시물', content: '두 번째 게시물 내용입니다.' },
    3: { title: '세 번째 게시물', content: '세 번째 게시물 내용입니다.' },
  }[id];

  
  
  // 댓글 관리를 위한 상태
  const [comment, setComment] = useState('');
  const [comments, setComments] = useState([]);
  


  // 서버와 통신해서 이미 있는 코드를 불러오고 댓글 추가해서 서버 SQL 댓글 테이블에 올릴 코드




  //// ★★★★★ 이거 댓글 id 부여하면 안됨, 일단 지금은 이대로 쓰는데 꼭 바꿔야 됨, 서버에서 게시글 테이블안에 댓글 컬럼을 받아와서 나열할거임
  // 댓글 추가 핸들러
  const handleAddComment = () => {
    if (comment.trim() !== '') {
      setComments([...comments, comment]);
      setComment('');
    }
  };



  // 찾는 post가 없다면 찾을 수 없습니다. 404
  if (!post) {
    return <div>게시물을 찾을 수 없습니다.</div>;
  }


  return (
    <div>
      <h1>{post.title}</h1>
      <p>{post.content}</p>

      <div>
        <h2>댓글</h2>
        <textarea
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          placeholder="댓글을 입력하세요"
        ></textarea>
        <button onClick={handleAddComment}>댓글 추가</button>
        <ul>
          {comments.map((c) => (
            <li key={c.id}>
                <div>
                  <textarea
                  ></textarea>
                </div>
              ) : (
                <div>
                  {c.content}
                  
                </div>
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default PostPage;
