import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const RegisterPage = () => {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [nickname, setNickname] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // 비밀번호 확인
    if (password !== confirmPassword) {
        alert('비밀번호가 일치하지 않습니다.');
        return;
    }



    // 서버와 통신해서 이미 있는 닉네임이면 alert 뜨는 코드
    // 임시로 사용할 닉네임 목록
    const existingNicknames = ['JohnDoe', 'JaneDoe', 'User123'];
    // 임시 닉네임 중복 확인 로직
    if (existingNicknames.includes(nickname)) {
        alert('이미 존재하는 닉네임입니다.');
        return;
    }
    /* 실제로 쓸 통신 코드
    
    try {
      // 닉네임 중복 확인을 위한 서버 요청
      const response = await fetch('/api/check-nickname', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ nickname }),
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.message || '닉네임 확인에 실패했습니다.');
      }

      if (result.exists) {
        alert('이미 존재하는 닉네임입니다.');
        return;
      }

      // 회원가입 로직을 추가할 수 있습니다. 여기서는 간단하게 입력한 아이디와 비밀번호를 로그로 출력하겠습니다.
      console.log('아이디:', id);
      console.log('비밀번호:', password);
      console.log('닉네임:', nickname);

      // 회원가입 요청
      const registerResponse = await fetch('/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ id, password, nickname }),
      });

      const registerResult = await registerResponse.json();

      if (!registerResponse.ok) {
        throw new Error(registerResult.message || '회원가입에 실패했습니다.');
      }

      // 성공적으로 회원가입이 완료되었을 때
      alert('회원가입이 완료되었습니다.');
      window.location.href = '/login'; // 로그인 페이지로 이동

        } catch (error) {
            alert(error.message);
        }
    };
    
    */

  };



    // 여기에 실제 통신할 코드 쓰기 fetch 또는 async



    // 이미 아이디가 중복되면 있으면 에러뜨게 만드는 코드





  return (
    <div>
      <h2>회원가입</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>아이디:</label>
          <input type="text" value={id} onChange={(e) => setId(e.target.value)} />
        </div>
        <div>
          <label>닉네임:</label>
          <input type="text" value={nickname} onChange={(e) => setNickname(e.target.value)} />
        </div>
        <div>
          <label>비밀번호:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <div>
          <label>비밀번호 확인:</label>
          <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />
        </div>
        <button type="submit">가입하기</button>
      </form>
      <p>이미 계정이 있으신가요? <Link to="/loginPage">여기를 클릭하여 로그인하세요</Link>.</p>
    </div>
  );
};

export default RegisterPage;
