import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const RegisterPage = () => {
    const navigate = useNavigate(); // useNavigate 훅을 사용

  
  
    const [id, setId] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [nickname, setNickname] = useState('');

    // 상태값을 이용하여 사용자 정보 설정
    const userData = {
        username: id,
        password: password,
        confirmPassword: confirmPassword,
        nickname: nickname
    };


  
    const handleSubmit = (e) => {
        e.preventDefault();
        // 비밀번호 확인
        if (password !== confirmPassword) {
            alert('비밀번호가 일치하지 않습니다.');
            return;
        }



        // 서버와 통신해서 이미 있는 닉네임이면 alert 뜨는 코드
        
        
        
        // 실제로 쓸 통신 코드, fetch를 사용한 POST 요청
        fetch('http://localhost:8000/registerpage/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData),
        })
        .then(response => response.json())
        .then(data => {
            console.log('Success:', data);
        })
        .catch((error) => {
            console.error('Error:', error);
        });



        // 여기에 실제 통신할 코드 쓰기 fetch 또는 async



        // 이미 아이디가 중복되면 있으면 에러뜨게 만드는 코드



        // 성공적으로 회원가입이 완료되었을 때
        alert('회원가입이 완료되었습니다.');
        navigate('/loginPage'); // 로그인 페이지로 이동  
    };


  return (
    <div>
      <h2>회원가입</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>아이디:</label>
          <input type="text" value={id} onChange={(e) => setId(e.target.value)} />
        </div>
        <div>
          <label>닉네임:</label>
          <input type="text" value={nickname} onChange={(e) => setNickname(e.target.value)} />
        </div>
        <div>
          <label>비밀번호:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <div>
          <label>비밀번호 확인:</label>
          <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />
        </div>
        <button type="submit">가입하기</button>
      </form>
      <p>이미 계정이 있으신가요? <Link to="/loginPage">여기를 클릭하여 로그인하세요</Link>.</p>
    </div>
  );
};

export default RegisterPage;
