import React, { useState, useRef, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';


const MyPage = () => {
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);    // 닉네임 옆의 연필버튼의 드롭다운 on off 버튼
    const dropdownRef = useRef(null);                               // 찾아봐야함
    const navigate = useNavigate();

    
    
    
    // ※ 초기화는 서버에서 받아와야함 이거 임시임
    const [username, setUsername] = useState('JohnDoe'); // 임시로 사용자 이름 설정
    const [nickname, setNickname] = useState('John'); // 임시로 사용자 닉네임 설정
    const [newNickname, setNewNickname] = useState(''); // 새 닉네임 입력 상태 추가
    const [userPosts, setUserPosts] = useState([
        { id: 1, title: '첫 번째 게시물', content: '첫 번째 게시물 내용입니다.' },
        { id: 2, title: '두 번째 게시물', content: '두 번째 게시물 내용입니다.' },
        { id: 3, title: '세 번째 게시물', content: '세 번째 게시물 내용입니다.' }
    ]); // 임시로 사용자가 작성한 게시물 목록 설정




    // 로그아웃 기능 구현
    const handleLogout = () => {
        localStorage.removeItem('isLoggedIn'); // 예를 들어, 로그인 상태를 localStorage에 저장했다면 삭제
        navigate('/');
    };

    // 회원탈퇴 기능 구현
    const handleWithdrawal = () => {
        localStorage.removeItem('userData'); // 예를 들어, 사용자 정보를 localStorage에 저장했다면 삭제
        navigate('/'); 
    };


    //// 여기부터 닉네임 관련 코드 
    const handleNicknameChange = () => {
        if (newNickname) {
            setNickname(newNickname);
            setNewNickname('');
            setIsDropdownOpen(false);
            // 서버로 새 닉네임을 업데이트하는 로직 추가 필요
        }
    };
    
    // 드롭다운이 on이면 off로 off면 on으로 바꾸는 코드
    const toggleDropdown = () => {
        setIsDropdownOpen(!isDropdownOpen);             
    }

    // 드롭다운이 OPEN일 시 외부를 클릭하면 자동으로 CLOSE 되는 설정
    const handleClickOutside = (event) => {
        if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
            setIsDropdownOpen(false);
        }
    };


    useEffect(() => {
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);


    return (
        <div>
            <h2>내 정보</h2>
            <p>아이디: {username}</p>
            <div style={{ position: 'relative', display: 'inline-block' }}>
                <p>닉네임: {nickname} <button onClick={toggleDropdown}>▼</button></p>
                {isDropdownOpen && (
                    <div ref={dropdownRef} style={{
                        position: 'absolute',
                        right: 0,
                        backgroundColor: '#f1f1f1',
                        boxShadow: '0px 8px 16px 0px rgba(0,0,0,0.2)',
                        zIndex: 1,
                        padding: '10px'
                    }}>
                        <input
                            type="text"
                            value={newNickname}
                            onChange={(e) => setNewNickname(e.target.value)}
                            placeholder="새 닉네임"
                            style={{ marginBottom: '10px' }}
                        />
                        <button onClick={handleNicknameChange}>닉네임 변경</button>
                    </div>
                )}
            </div>

            <h2>내가 쓴 글</h2>
            <ul>
                {userPosts.map(post => (
                    <li key={post.id}>
                        <h3>{post.title}</h3>
                        <p>{post.content}</p>
                    </li>
                ))}
            </ul>

            <button onClick={handleLogout}>로그아웃</button>
            <button onClick={handleWithdrawal}>회원탈퇴</button>
        </div>
    );
};

export default MyPage;
