import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const LoginPage = () => {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    

    // 서버에서 실패하면 각 실패 사항에 따라 다른 에러메시지를 리턴함
    try {
        const response = await fetch('http://localhost:8000/loginpage/', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ id, password }),
        });
  
        // 서버에서 보내주는 response = return을 읽고 변수에 저장합니다.
        const data = await response.json();
        console.log('로그인 성공:', data);
        

        navigate('/'); // 메인페이지로 이동  

      } catch (error) {
        console.error('로그인 오류1:', error.message);
        alert(error.message); // 에러 메시지를 알림창으로 표시
      }

  };


  return (
    <div>
      <h2>로그인</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>ID:</label>
          <input type="text" value={id} onChange={(e) => setId(e.target.value)} />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <button type="submit">로그인</button>
      </form>
      <p>아직 계정이 없으신가요? <Link to="/registerpage">여기를 클릭하여 회원가입하세요</Link>.</p>
    </div>
  );
};

export default LoginPage;
