import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';


const LoginPage = () => {
    const navigate = useNavigate();
    const [userid, setUserid] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const { login } = useAuth(); // AuthContext에서 제공하는 login 메서드 가져옴

    
    const handleSubmit = async (e) => {
        e.preventDefault();
    
        const formData = {
            userid: userid,
            password: password,
        };
    
        try {
            const response = await fetch('http://localhost:8080/api/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json' // JSON 데이터를 전송할 것임을 명시
                },
                body: JSON.stringify(formData)
            });
    
            if (response.ok) {
                const responseData = await response.text(); // 문자열로 응답 받기
                console.log('로그인 성공:', responseData);
                navigate('/');
            } else if (response.status === 401) {
                setErrorMessage('아이디나 비밀번호가 올바르지 않습니다.');
            } else {
                setErrorMessage('로그인 중 오류가 발생했습니다. 다시 시도해주세요.');
            }
        } catch (error) {
            console.error('로그인 오류:', error.message);
            setErrorMessage('로그인 중 오류가 발생했습니다. 다시 시도해주세요.');
        }
    };
    

    return (
        <div>
            <h2>로그인</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>ID:</label>
                    <input type="text" value={userid} onChange={(e) => setUserid(e.target.value)} />
                </div>
                <div>
                    <label>Password:</label>
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                </div>
                <button type="submit">로그인</button>
            </form>
            <p style={{ color: 'red' }}>{errorMessage}</p>
            <p>아직 계정이 없으신가요? <Link to="/register">여기를 클릭하여 회원가입하세요</Link>.</p>
        </div>
    );
};

export default LoginPage;
