import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

// 상단 게시판 카테고리 모음
const categories = ['자유게시판', '공감게시판', 'BEST게시판'];

const MainPage = () => {
    
    // 처음 초기값 게시판을 자유게시판으로 만들기 위하 코드, 만약 '공감게시판'이면 새로고침할 때마다 공감게시판이 처음 보임
    const [selectedCategory, setSelectedCategory] = useState('자유게시판');
    const [filteredPosts, setFilteredPosts] = useState(null); // 선택된 카테고리에 맞는 게시글만 저장하는 상태

    useEffect(() => {
       // 예시로 fetch API를 사용한 GET 요청
        const fetchPosts = async () => {
            try {
                const response = await fetch('http://localhost:8000/');
                if (!response.ok) {
                    throw new Error('Failed to fetch posts');
                }
                const data = await response.json();
                console.log('Fetched posts:', data);
                setFilteredPosts(data); // 받아온 데이터를 filteredPosts에 설정
            } catch (error) {
                console.error('Error fetching posts:', error);
            }
        };

        fetchPosts();
    }, [selectedCategory]);

    const handleCategoryChange = (category) => {
        setSelectedCategory(category);
    };



    return (
        <div>
            <h1>메인 페이지</h1>
            <div style={{ marginBottom: '20px' }}>
                {categories.map(category => (
                    <button 
                        key={category} 
                        onClick={() => handleCategoryChange(category)} // 카테고리 변경 핸들러 추가
                        style={{ margin: '0 10px' }}
                    >
                        {category}
                    </button>
                ))}
            </div>

            <Link to="/PostWrite">글쓰기</Link>
            <Link to="/LoginPage">로그인</Link>
            <Link to="/MyPage">마이페이지</Link>

            <ul>
            {filteredPosts === null ? (
    <li key="loading">Loading...</li>
) : filteredPosts.length === 0 ? (
    <li key="no-posts">No posts found for selected category</li>
) : (
    filteredPosts.map(post => (
        <li key={post.id}>
            <h2>{post.title}</h2>
            <p>{post.content}</p>
        </li>
    ))
)}  
            </ul>
        </div>
    );
};

export default MainPage;
