import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const LoginPage = () => {
    const navigate = useNavigate(); // useNavigate 훅을 사용
    const { login } = useAuth();    // useAuth를 사용하여 로그인 상태를 관리

    // 초기 상태 설정
    const [userid, setUserid] = useState('');
    const [password, setPassword] = useState('');

    // form 제출 핸들러
    const handleSubmit = async (e) => {
        e.preventDefault();

        // LoginRequest 객체 생성
        const loginData = {
            userid: userid,
            password: password,
        };

        try {
            const response = await fetch('http://localhost:8080/api/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json', // JSON 형식으로 데이터 전송
                },
                body: JSON.stringify(loginData), // LoginRequest 객체를 JSON 문자열로 변환하여 전송
            });

            const data = await response.json();

            if (response.ok) {
                login(data.userid); // 로그인 상태 업데이트
                navigate('/'); // 메인페이지로 이동
            } else {
                console.log('로그인 실패');
                alert('아이디 혹은 패스워드를 확인해주세요');
            }

        } catch (error) {
            console.error('로그인 오류:', error.message);
            alert('로그인 중 오류가 발생했습니다. 다시 시도해주세요.'); // 에러 메시지를 알림창으로 표시
        }
    };

    return (
        <div>
            <h2>로그인</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>ID:</label>
                    <input type="text" value={userid} onChange={(e) => setUserid(e.target.value)} />
                </div>
                <div>
                    <label>Password:</label>
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                </div>
                <button type="submit">로그인</button>
            </form>
            <p>아직 계정이 없으신가요? <Link to="/registerpage">여기를 클릭하여 회원가입하세요</Link>.</p>
        </div>
    );
};

export default LoginPage;
