import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../components/Auth/AuthContext';

const categories = ['자유게시판', '공감게시판', 'BEST게시판'];

const PostWrite = () => {
    const navigate = useNavigate(); // useNavigate 훅 초기화
    const { isLogin, userId, nickname } = useAuth();
    
    const [category, setCategory] = useState('');
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [imageFile, setImageFile] = useState(null);

    const handleFileChange = (event) => {
        setImageFile(event.target.files[0]);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        const postRequest = {
            id: userId,
            category,
            title,
            content,
        };
        formData.append('post', new Blob([JSON.stringify(postRequest)], { type: 'application/json' }));
        if (imageFile) {
            formData.append('imageFile', imageFile);
        }

        try {
            const response = await fetch('http://localhost:8080/api/jayuposts', {
                method: 'POST',
                body: formData,
            });
            
            if (!response.ok) {
                throw new Error('Failed to submit the post');
            }

            const result = await response.json();
            console.log('Post submitted successfully:', result);
            
            navigate('/');
        } catch (error) {
            console.error('Error submitting post:', error);
        }
    };

    return (
        <div>
            <h1>글쓰기</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>카테고리:</label>
                    <select value={category} onChange={(e) => setCategory(e.target.value)}>
                        <option value="">카테고리를 선택하세요</option>
                        {categories.map((cat, index) => (
                            <option key={index} value={cat}>{cat}</option>
                        ))}
                    </select>
                </div>
                <div>
                    <label>제목:</label>
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </div>
                <div>
                    <label>내용:</label>
                    <textarea value={content} onChange={(e) => setContent(e.target.value)} />
                </div>
                <div>
                    <label>이미지:</label>
                    <input type="file" onChange={handleFileChange} />
                </div>
                <button type="submit">글쓰기</button>
            </form>
        </div>
    );
};

export default PostWrite;
